/**
 * @example
 * underscoreName('getList');
 * => get_list
 */
export declare function underscore(string: any): any;
/**
 * @example
 * camelize('font-size');
 * => fontSize
 */
export declare function camelize(string: any): any;
/**
 * @example
 * camelize('fontSize');
 * => font-size
 */
export declare function hyphenate(string: any): any;
/**
 * @example
 * merge('{0} - A front-end {1} ','Suite','framework');
 * => Suite - A front-end framework
 */
export declare function merge(pattern: any): any;
